import React from 'react'
import ReactDom from 'react-dom';

import './assets/css3/transition.css'

class App extends React.Component{
    state={
        b:false
    };
    render(){
        return(
            <div>
                <input type="button" value="按钮" onClick={()=>this.setState({b:this.state.b})}/>
                    {this.state.b && <div className={"box"}></div>}
            </div>
        )
    }

}






ReactDom.render(
    <App/>,
    document.querySelector("#root"),
    ()=>console.log('render is over')
)
