import React from 'react'
import ReactDom from 'react-dom';

class ToDo extends React.Component {
    //处理数据
    state={
        usename:'',
        content:'',
        list:[
            {id:1,name:'alex',content:'alex123'}
        ]
    };
    timer = null //定义一个实例属性

    //定义增，删，改，查
    send = () =>{
        let arr = [...this.state.list];
        arr.push({id:arr.length+1,name:this.state.name,content:this.state.content});
        this.setState({
            list:arr,
            name:'',
            content:''
        })
    };
    del = (index,id) =>{
        this.state.list.splice(index,1);
        this.setState({list:this.state.list})
    };
    check = (index,id) =>{
        this.state.list[index].content="bwn";
        this.setState({list:this.state.list});
    };
    clean = () =>{
        this.setState({list:[]})
    };
    render(){
        return(
            <div>
                <h3>留言板</h3>
                <input type="text" value={this.state.name} onChange={(ev)=>this.setState({name:ev.target.value})}/>
                <br/>
                <input type='text' value={this.state.content} onChange={(ev)=>this.setState({name:ev.target.value})}/>
                <br/>
                <input type="button" value="提交" onClick={this.send}/>
                <br/>
                <ul>
                    {this.state.list.map((item,index)=>(

                        <li key={item.id}>
                            <span>{item.content}</span>
                            ---
                            <em>{item.id}</em>
                            ---
                            <a href='#' onClick={this.del.bind(null,index,item.id)}>删除</a>
                            <a href="#" onClick={this.check.bind(null,index,item.id)}>修改</a>
                        </li>
                    ))
                    }
                </ul>
                {
                    this.state.list.length !== 0 ?
                        <input type="button" value="清空" onClick={this.clean}/> : null
                }
            </div>
        )
    }
}

ReactDom.render(
    <ToDo/>,
    document.querySelector("#root")


)
